#!/bin/bash
# converts CC BY place names from the geographical authority GML data to omorfi

SED="sed"

for f in nimisto_oapif/places/etrs89/gml/places_202*; do
    mkdir "$(basename "$f")"
    unzip -d "$(basename "$f")" "$f"
done
for d in places_* ; do
    saxon9-transform -s:"$d/places.xml" gml2omorfi.xslt
done |\
